            }
          },
          "repository": {
            "name": "demo",
            "type": "repository",
            "full_name": "my-workspace/demo",
            "links": {
              "self": {
                "href": "https://api.bitbucket.org/2.0/repositories/my-workspace/demo"
              },
              "html": {
                "href": "https://bitbucket.org/my-workspace/demo"
              },
