      // categories.
      "order": "int",

      // The ID that uniquely identifies this category.
      "id": "guid",

      // Internal use only. Do not use.
      "contentId": "guid"
    },

    // Whether this commendation is enabled or not.
    "enabled": "boolean",

    // The ID that uniquely identifies this commendation.
    "id": "guid",

      // Internal use only. Do not use.
    "contentId": "guid"
  }
]

